/*@xopowo 2018
 * 20180128 初始完成
 * 20180526 調整一些小地方
 */
.SquareCssResource-chatContent,
.SquareCssResource-dockPanel,
.SquareCssResource-squareHeader,
.SquareCssResource-watchPanel .SquareCssResource-odd
{
    background: #222222!important;
}
.SquareCssResource-chatContent.SquareCssResource-even {
    background: #2F2F2F!important;
}
.SquareCssResource-message,
.gwt-InlineHTML,
.SquareCssResource-inputAreaLabel,
.gwt-MenuItem {
    color: #aaaaaa!important;
}
.SquareCssResource-inputArea {
    background: #373737!important;
}
.google-visualization-table-table > tbody > tr,
.google-visualization-table-table > thead > tr {
    background: #2d2d30!important;
    color: #aaaaaa!important;
}

/*投票表格*/
.google-visualization-table-table td,
.google-visualization-table-table th {
    border: 1px solid #373737;
}
.gradient {
    background-image: none!important;
}

/*上方選單*/
.gwt-MenuBar-horizontal {
    background: #3f3f46;
    border-color: #3f3f46;
}
.gwt-MenuItem:hover {
    background: #3f3f46;
    color: white!important;
}
.gwt-MenuItem.gwt-MenuItem-selected{
    background:#1b1b1c;
}
/*上方選單選項之間分隔線*/
.gwt-MenuItemSeparator,
.menuSeparatorInner,.menuPopupMiddleCenter {
    background-color: #3f3f46!important;
}
/*上方子選單*/
.menuPopupMiddle,.menuPopupMiddleLeft,.menuPopupMiddleRight,.menuPopupBottom>td,.menuPopupTop>td,.gwt-MenuBar-vertical{
    border-color:#1b1b1c!important;
    background:#1b1b1c!important;
}
.gwt-MenuBarPopup,.menuPopupMiddleCenterInner .menuPopupContent,.gwt-MenuBar-vertical{
    background: #3f3f46;
}
:root {
    --chat-name-shadow-color: #fffefe; 
}
/*user name & 投票標題的user name*/
.gwt-HTML.SquareCssResource-chatName,
.SquareCssResource-voteTitle > .gwt-InlineHTML > .GlobalCssResource-colorNickname{
    text-shadow:
-1px -1px 0 var(--chat-name-shadow-color),
1px -1px 0 var(--chat-name-shadow-color),
-1px 1px 0 var(--chat-name-shadow-color),
1px 1px 0 var(--chat-name-shadow-color),
2px 2px 2px var(--chat-name-shadow-color);
}
/*投票視窗的名字框*/
.dialogMiddleCenterInner.dialogContent > table > tbody > tr > td > div > div > table > tbody > tr > td > div.gwt-HTML.SquareCssResource-chatName {
    background: #2F2F2F!important;
}
.SquareCssResource-chatContent.SquareCssResource-replyToMe {
    background: #364c3b!important;
}

/*左上角的數字框*/
.GlobalCssResource-colorBox {
    background-color: #2F2F2F!important;
}

/*下拉選單*/
select {
    color: #aaaaaa;
    background-color: #1b1b1c;
    border-color: #333337;
}

/*右方好料訊息標題框*/
.SquareCssResource-mediaHeader {
    background: #2F2F2F!important;
    color: #aaaaaa!important;
}

/*超連結文字*/
a {
    color: #0097fb;
}
a:visited {
    color: #88ccfe;
}

/*海報背景*/
.SquareCssResource-posterPanel {
    background-color: #28282b;
}
.SquareCssResource-posterContent {
    color: #aaaaaa!important;
}
/*開窗標題*/
.gwt-DialogBox{
    color:#aaaaaa;
    background-color:#007acc;
    border:1px solid #007acc;
}
.gwt-DialogBox .dialogTopLeft,
.gwt-DialogBox .dialogTopRight,
.gwt-DialogBox .dialogMiddleLeft,
.gwt-DialogBox .dialogMiddleRight,
.gwt-DialogBox .dialogBottomLeft,
.gwt-DialogBox .dialogBottomCenter,
.gwt-DialogBox .dialogBottomRight{
    display:none;
}
.gwt-DialogBox .dialogTopCenter,.dialogTopCenterInner{
    background-color:#007acc;
    border:1px solid #007acc;
}
.gwt-DialogBox .Caption{
    border:1px solid #007acc;
    background:#007acc;
    color:white;
}
.dialogMiddle>td{
    background: #2F2F2F!important;
}
/*首頁背景*/
body,.HomeCssResource-homeHeader{
    background-color: #28282b;
}
/*首頁主播廣場標題*/
.HomeCssResource-createSquare>table>tbody>tr>td:nth-child(2)>.GlobalCssResource-colorBox>table>tbody>tr>td>div.gwt-HTML>div{
color:#1976D2;
}
