.SquareCssResource-chatContent,
.SquareCssResource-dockPanel,
.SquareCssResource-squareHeader,
.SquareCssResource-watchPanel .SquareCssResource-odd
{
    background: #222222!important;
}
.SquareCssResource-chatContent.SquareCssResource-even {
    background: #2F2F2F!important;
}
.SquareCssResource-message,
.gwt-InlineHTML,
.SquareCssResource-inputAreaLabel,
.gwt-MenuItem {
    color: #aaaaaa!important;
}
.SquareCssResource-inputArea {
    background: #373737!important;
}
.google-visualization-table-table > tbody > tr,
.google-visualization-table-table > thead > tr {
    background: #2d2d30!important;
    color: #aaaaaa!important;
}

/*投票表格*/
.google-visualization-table-table td,
.google-visualization-table-table th {
    border: 1px solid #373737;
}
.gradient {
    background-image: none!important;
}

/*上方選單*/
.gwt-MenuBar-horizontal {
    background: #3f3f46;
    border-color: #3f3f46;
}
.gwt-MenuItem:hover {
    background: #3f3f46;
    color: white!important;
}
.gwt-MenuItem.gwt-MenuItem-selected{
    background:#1b1b1c;
}
/*上方選單選項之間分隔線*/
.gwt-MenuItemSeparator,
.menuSeparatorInner,.menuPopupMiddleCenter {
    background-color: #3f3f46!important;
}
/*上方子選單*/
.menuPopupMiddle,.menuPopupMiddleLeft,.menuPopupMiddleRight,.menuPopupBottom>td,.menuPopupTop>td,.gwt-MenuBar-vertical{
    border-color:#1b1b1c!important;
    background:#1b1b1c!important;
}
.gwt-MenuBarPopup,.menuPopupMiddleCenterInner .menuPopupContent,.gwt-MenuBar-vertical{
    background: #3f3f46;
}

